DECLARE
	v_total_payts	NUMBER(16,2);
	v_ar_amt		NUMBER(16,2);
	v_message		VARCHAR2(500);
	v_tran_type		ACIT_ACKGT_RECEIPT.TRAN_TYPE_CD%TYPE;
	v_alloted_amt   ACIT_AR_TRANSUMM.ALLOTED_AMT%TYPE;
BEGIN
	SELECT NVL(SUM(TOTAL_LOCAL_AMT), 0)
	  INTO v_total_payts
	  FROM ACIT_AR_TRANSUMM
	 WHERE TRAN_ID = #{tranId};

	SELECT AR_AMT
	  INTO v_ar_amt
	  FROM ACIT_ACKGT_RECEIPT
	 WHERE TRAN_ID = #{tranId};

	 SELECT TRAN_TYPE_CD
	   INTO v_tran_type
	   FROM ACIT_ACKGT_RECEIPT
	  WHERE TRAN_ID = #{tranId};





		ACIT_AR_TRANSUMM_PKG.save_acit_transumm(#{tranId},
												#{billId},
												#{billType},
												#{totalLocalAmt},
												null,
												#{createUser},
												CAST(to_timestamp(#{createDate}, 'YYYY-MM-DD"T"HH24:MI:SS') AS DATE),
												#{updateUser},
												CAST(to_timestamp(#{updateDate}, 'YYYY-MM-DD"T"HH24:MI:SS') AS DATE)
												);


			ACIT_AR_TRANSDTL_PKG.del_ar_transdtl(
												#{del.tranId},
												#{del.billId},
												#{del.itemNo}				
			);




			ACIT_AR_TRANSDTL_PKG.save_ar_transdtl(
												#{save.tranId},
												#{save.billId},
												#{save.itemNo, jdbcType=VARCHAR},
												#{save.transdtlType, jdbcType=VARCHAR},
												#{save.itemName},
												#{save.currCd},
												#{save.currRate},
												#{save.currAmt},
												#{save.localAmt},
												#{save.refNo, jdbcType=VARCHAR},
												#{save.remarks, jdbcType=VARCHAR},
												#{save.createUser},
												CAST(to_timestamp(#{save.createDate}, 'YYYY-MM-DD"T"HH24:MI:SS') AS DATE),
												#{save.updateUser},
												CAST(to_timestamp(#{save.updateDate}, 'YYYY-MM-DD"T"HH24:MI:SS') AS DATE)				
			);




		IF v_tran_type = 1 THEN
		 	SELECT v_ar_amt -  SUM(a.TOTAL_LOCAL_AMT/b.CURR_RATE)
		 	  INTO v_alloted_amt
			  FROM ACIT_AR_TRANSUMM a, ACIT_ACKGT_RECEIPT b
	         WHERE a.TRAN_ID = #{tranId}
	           AND a.TRAN_ID = b.TRAN_ID
			   AND a.BILL_ID IN (2,3);

			ACIT_AR_TRANSUMM_PKG.save_acit_transumm(#{tranId},
													1,
													v_tran_type,
													null,
													v_alloted_amt,
													#{createUser},
													CAST(to_timestamp(#{createDate}, 'YYYY-MM-DD"T"HH24:MI:SS') AS DATE),
													#{updateUser},
													CAST(to_timestamp(#{updateDate}, 'YYYY-MM-DD"T"HH24:MI:SS') AS DATE)
													);
		 ELSE
		 	v_alloted_amt := NULL;
	     END IF;
